Assets>\n	<#if getterUtil.getBoolean(enableRelatedAssets)>\n		<@liferay_asset[\"asset-links\"]\n			assetEntryId=entry.getEntryId()\n			viewInContext=!stringUtil.equals(assetLinkBehavior, \"showFullContent\")\n		/>\n	</#if>\n</#macro>\n\n<#macro getSocialBookmarks>\n	<@liferay_social_bookmarks[\"bookmarks\"]\n		className=entry.getClassName()\n		classPK=entry.getClassPK()\n		displayStyle=\"${socialBookmarksDisplayStyle}\"\n		target=\"_blank\"\n		title=entry.getTitle(locale)\n		url=viewURL\n	/>\n</#macro>',0,0,33307,NULL,NULL),(0,'ca78f2ed-4b52-9f2f-2ff9-66b135ffd39f',33609,20128,20101,20105,NULL,20105,NULL,'2020-12-24 22:43:02.764000','2020-12-24 22:43:02.764000',25710,33603,20401,'BASIC-WEB-CONTENT','1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,sv_SE,pt_BR,ja_JP,fr_FR,hu_HU,de_DE,ca_ES,ar_SA,fi_FI,zh_CN,es_ES,nl_NL\" default-locale=\"en_US\"><Name language-id=\"sv_SE\">Vanligt innehll</Name><Name language-id=\"pt_BR\">Contedo Web bsico</Name><Name language-id=\"ja_JP\">Web</Name><Name language-id=\"fr_FR\">Contenu web de base</Name><Name language-id=\"hu_HU\">Alapvet webtartalom</Name><Name language-id=\"de_DE\">Einfacher Webcontent</Name><Name language-id=\"ca_ES\">Contigut web bsic</Name><Name language-id=\"ar_SA\"> </Name><Name language-id=\"fi_FI\">Tavallinen web-sislt</Name><Name language-id=\"en_US\">Basic Web Content</Name><Name language-id=\"zh_CN\"> Web </Name><Name language-id=\"es_ES\">Contenido web bsico</Name><Name language-id=\"nl_NL\">Basiswebcontent</Name></root>','','display','create','ftl','${content.getData()}',1,0,33610,NULL,NULL);
