ELECTOR = \'.navbar-site\';\n\n		if (navigation) {\n			navigation.plug(Liferay.NavigationInteraction);\n		}\n	</@>\n</#if>',0,0,28008,NULL,NULL),(0,'33aa28e7-cdfa-181b-d1e2-95f250d70bbb',28011,20128,20101,20105,NULL,20105,NULL,'2020-12-24 22:42:53.662000','2020-12-24 22:42:53.662000',28010,0,26903,'DOCUMENTLIBRARY-CAROUSEL-FTL','1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,sv_SE,pt_BR,ja_JP,fr_FR,hu_HU,de_DE,ca_ES,ar_SA,fi_FI,zh_CN,es_ES,nl_NL\" default-locale=\"en_US\"><Name language-id=\"sv_SE\">Carousel</Name><Name language-id=\"pt_BR\">Carrossel</Name><Name language-id=\"ja_JP\">カルーセル</Name><Name language-id=\"fr_FR\">Carrousel</Name><Name language-id=\"hu_HU\">Körhinta</Name><Name language-id=\"de_DE\">Karussel</Name><Name language-id=\"ca_ES\">Carrussel</Name><Name language-id=\"ar_SA\">مكتبة</Name><Name language-id=\"fi_FI\">Karuselli</Name><Name language-id=\"en_US\">Carousel</Name><Name language-id=\"zh_CN\">Carousel</Name><Name language-id=\"es_ES\">Carrusel</Name><Name language-id=\"nl_NL\">Carrousel</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,sv_SE,pt_BR,ja_JP,fr_FR,hu_HU,de_DE,ca_ES,ar_SA,fi_FI,zh_CN,es_ES,nl_NL\" default-locale=\"en_US\"><Description language-id=\"sv_SE\">Visar bilder i carousel-format.</Description><Description language-id=\"pt_BR\">Exibe imagens num carrossel.</Description><Description language-id=\"ja_JP\">カルーセル内に画像を表示</Description><Description language-id=\"fr_FR\">Affiche les images dans un carrousel.</Description><Description language-id=\"hu_HU\">Megjeleníti a képeket körhintaszerűen.</Description><Description language-id=\"de_DE\">Darstellung von Bildern in einem Karussell.</Description><Description language-id=\"ca_ES\">Mostra imatges en format carrusel.</Description><Description language-id=\"ar_SA\">يعرض الصور في مكتبة.</Description><Description language-id=\"fi_FI\">Näyttää kuvat karusellissa.</Description><Description language-id=\"en_US\">Displays images in a carousel.</Description><Description language-id=\"zh_CN\">把图片显示在转盘中。</Description><Description language-id=\"es_ES\">Muestra imágenes en un carrusel.</Description><Description language-id=\"nl_NL\">Geeft afbeeldingen weer in een carrousel.</Description></root>','display',NULL,'ftl','<#if entries?has_content>\n	<style>\n		#<@portlet.namespace />carousel .carousel-item {\n			background-color: #000;\n			height: 250px;\n			overflow: hidden;\n			text-align: center;\n			width: 700px;\n		}\n\n		#<@portlet.namespace />carousel .carousel-item img {\n			max-height: 250px;\n			max-width: 700px;\n		}\n	</style>\n\n	<div id=\"<@portlet.namespace />carousel\">\n		<#assign imageMimeTypes = propsUtil.getArray(\"dl.file.entry.preview.image.mime.types\") />\n\n		<#list entries as entry>\n			<#if imageMimeTypes?seq_contains(entry.getMimeType())>\n				<div class=\"carousel-item image-viewer-base-image\">\n					<img src=\"${dlUtil.getPreviewURL(entry, entry.getFileVersion(), themeDisplay, \"\")}\" />\n				</div>\n			</#if>\n		</#list>\n	</div>\n\n	<@liferay_aui.script use=\"aui-carousel\">\n		var carousel = new A.Carousel(\n			{\n				after: {\n					responsive: function(event) {\n						event.stopImmediatePropagation();\n\n						var boundingBox = event.currentTarget.get(\'boundingBox\');\n\n						boundingBox.all(\'.image-viewer-base-image-list, .image-viewer-base-image\').setStyles(\n							{\n								height: \'auto\',\n								maxHeight: event.height,\n								maxWidth: event.width,\n								width: \'auto\'\n							}\n						);\n					}\n				},\n\n				contentBox: \'#<@portlet.namespace />carousel\',\n				height: 250,\n				intervalTime: 2,\n				width: 700\n			}\n		).render();\n	</@liferay_aui.script>\n</#if>',0,0,28012,NULL,NULL),(0,'18fd40e5-e74c-eb44-8714-626842a6ca83',28014,20128,20101,20105,NULL,20105,NULL,'2020-12-24 22:42:53.683000','2020-12-24 22:42:53.683000',20013,0,26903,'ASSET-CATEGORIES-NAVIGATION-MULTI-COLUMN-LAYOUT-FTL','1.0','<?xml version=\'1.0\' encoding=\